  .layout > svg {
    display: block;
    max-inline-size: 150px;
    block-size: auto;
    align-self: end;
  }

  .layout > div {
    flex-basis: 0;
    min-inline-size: 20ch;
    flex-grow: 999;
  }

  h1 {
    font-size: var(--size-step-3);
  }

  p {
    text-wrap: pretty;
  }

  svg > g {
    animation: translate 4.2s 0.05s forwards step-start;
  }

  @keyframes translate {
    0% {
      translate: -32px 0px;
    }
    20% {
      translate: 0px 0px;
    }
    40% {
      translate: -32px 0px;
    }
    60% {
      translate: 0px 0px;
    }
    87% {
      translate: -32px 0px;
    }
    100% {
